$(".button").click(function(data) {

    var movie = { 
        Title: $('#Title').val(), 
        ReleaseDate: $('#ReleaseDate').val(),
        Genre: $('#Genre').val(),
        Price: $('#Price').val(),
        Rating: $('#Rating').val()
    };

    $.ajax(
    {
        url: "Create",
        type: "POST",
        data: movie,
        success: function(response) 
        {
            $("#result").html(response.message);
        },
        error: function(result) {
            alert("ERROR " + result.status + ' ' + result.statusText);
        }
    });
});